sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

          // Add your code here
var rightNow = new GlideDateTime();
var tasks = new GlideRecord("x_58872_needit_needit_task");
tasks.addQuery('due_date','<=',rightNow);
tasks.addQuery('state','<',3);
tasks.addQuery('assigned_to', current.assigned_to);
tasks.query();

if (tasks.getRowCount() > 1) {
template.print("In addition to this overdue NeedIt Task, you have " + (tasks.getRowCount()-1) + " more overdue NeedIt Tasks.");
}


})(current, template, email, email_action, event);
